targets.expr:
max by(id, name, container_label_com_docker_compose_project, container_label_com_docker_compose_service, pod, uid, node, container, image) (
  max_over_time(container_start_time_seconds{instance="$instance", id=~"$id"}[$__range])
)

max by(id, name) (
  max without(uid) (max_over_time(container_last_seen{instance="$instance", id=~"$id"}[$__range]))
) * 1000

sum(
  sum without(cpu) (
    rate(container_cpu_usage_seconds_total{instance="$instance", id=~"$id", cpu="total"}[$__rate_interval])
  )
  /
  (
    container_spec_cpu_quota{instance="$instance", id=~"$id"}
    /
    container_spec_cpu_period{instance="$instance", id=~"$id"}
  )
)
