label_replace(
sum by(id) (
  container_spec_cpu_quota{instance="$instance", id=~"$id"}
  /
  container_spec_cpu_period{instance="$instance", id=~"$id"}
)
, "id", "$1", "id", "$id_prefix/?(.*)"
)

label_replace(
max by (id) (
  (rate(container_cpu_cfs_throttled_seconds_total{instance="$instance", id=~"$id"}[$__rate_interval]) )
)
, "id", "$1", "id", "$id_prefix/?(.*)"
)

label_replace(
sum by(id) (
    sum without(cpu) (
        rate(container_cpu_usage_seconds_total{instance="$instance", id=~"$id", cpu="total"}[$__rate_interval])
    )
    /
    (
      container_spec_cpu_quota{instance="$instance", id=~"$id"}
      /
      container_spec_cpu_period{instance="$instance", id=~"$id"}
    )
)
, "id", "$1", "id", "$id_prefix/?(.*)"
)

