targets.expr:
max by(pod, container, name, instance) (
  max_over_time(container_start_time_seconds{location="$location", namespace="$namespace", pod=~"$pod", container=~"$container", name=~"$id"}[$__range])
) * 1000

max by($split_ids) (
  (rate(container_cpu_cfs_throttled_seconds_total{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}[$__rate_interval]) )
)

max by($split_ids) (
  (rate(container_cpu_cfs_throttled_seconds_total{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}[$__rate_interval]) * $__interval_ms / 1000 )
)

max by(container) (
    kube_pod_container_resource_requests{location="$location", namespace="$namespace", pod="$pod", resource="cpu", container=~"$container", uid=~"$pod_uid"}
)

max by($split_ids) (
  (rate(container_cpu_cgroup_burst_seconds_total{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}[$__rate_interval]) * $__interval_ms / 1000 )
)

sum by($split_ids) (rate(container_cpu_usage_seconds_total{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id", cpu="total"}[$__rate_interval]))
/ on(container) group_left
sum by(container) (
    kube_pod_container_resource_requests{location="$location", namespace="$namespace", pod="$pod", resource="cpu",container=~"$container"}
)

sum by(container) (
    kube_pod_container_cgroup_burst{location="$location", namespace="$namespace", pod="$pod", container=~"$container"}
)

max by($split_ids) (
  container_spec_cpu_quota{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}
  /
  container_spec_cpu_period{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}
)

sum by($split_ids) (
    sum without(cpu) (
        rate(container_cpu_usage_seconds_total{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id", cpu="total"}[$__rate_interval])
    )
    /
    (
      container_spec_cpu_quota{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id", cpu="total"}
      /
      container_spec_cpu_period{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id", cpu="total"}
    )
)
or
# when container_spec_cpu_quota is missing
sum by($split_ids) (
    rate(container_cpu_usage_seconds_total{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id", cpu="total"}[$__rate_interval])
)
/ on(container) group_left
sum by(container) (
    kube_pod_container_resource_limits{location="$location", namespace="$namespace", pod="$pod", resource="cpu",container=~"$container"}
)

max by($split_ids) (
  avg_over_time(container_memory_total_active_file_bytes{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}[$__interval])
  or
  container_memory_total_active_file_bytes{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}
)

max by($split_ids) (
  avg_over_time(container_memory_usage_bytes{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}[$__interval])
  or
  container_memory_usage_bytes{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}
)
