targets.expr:
max by(pod, workload_type, workload) (
  last_over_time(namespace_workload_pod:kube_pod_owner:relabel{cluster="$cluster", namespace="$namespace", pod=~"$pod"}[$__range])
)

sum by(pod) (max by(pod, container) (
  rate(container_cpu_usage_seconds_total{cluster="$cluster", namespace="$namespace", pod=~"$pod"}[$__range])
))

sum by (pod) (max by(pod, container) (
  max_over_time(container_memory_rss{cluster="$cluster", namespace="$namespace", pod=~"$pod"}[$__range])
))

sum by (pod) (max by(pod, container) (
  max_over_time(container_memory_usage_bytes{cluster="$cluster", namespace="$namespace", pod=~"$pod"}[$__range])
))

max by(node, pod) (
  last_over_time(kube_pod_info{cluster="$cluster", namespace="$namespace", pod=~"$pod"}[$__range])
)
