targets.expr:
round((
time() -
max by(pod, uid) (
  last_over_time(container_last_seen_time{cluster="$cluster", namespace="$namespace", pod=~"$pod", uid=~"$pod_uid"}[$__range])
)
))

max by(pod, uid) (
  last_over_time(kube_pod_created{cluster="$cluster", namespace="$namespace", pod=~"$pod", uid=~"$pod_uid"}[$__range])
) * 1000
