targets.expr:
sum by(container) (
    kube_pod_container_resource_requests{cluster="$cluster", namespace="$namespace", pod="$pod", resource="cpu",container=~"$container", invalid=$enableLimits}
)

sum by(container) (
    kube_pod_container_resource_limits{cluster="$cluster", namespace="$namespace", pod="$pod", resource="cpu", container=~"$container", invalid=$enableLimits}
)

max by (container, name) (
  (rate(container_cpu_cgroup_burst_seconds_total{cluster="$cluster", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}[$__rate_interval]) )
)

sum by(container, name) (rate(container_cpu_usage_seconds_total{cluster="$cluster", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}[$__rate_interval]))
/ on(container) group_left
sum by(container) (
    kube_pod_container_resource_requests{cluster="$cluster", namespace="$namespace", pod="$pod", resource="cpu",container=~"$container", invalid=$enableLimits}
)

sum by(container, name) (rate(container_cpu_usage_seconds_total{cluster="$cluster", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}[$__rate_interval]))
/ on(container) group_left
sum by(container) (
    kube_pod_container_resource_limits{cluster="$cluster", namespace="$namespace", pod="$pod", resource="cpu",container=~"$container", invalid=$enableLimits}
)

max by (container, name) (
  last_over_time(
    (
      container_memory_rss{cluster="$cluster", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}
      / on(container) group_left(uid)
      kube_pod_container_resource_requests{cluster="$cluster", namespace="$namespace", pod="$pod", container=~"$container", resource="memory"}
    )
  [$__range:])
)

max by (container, name) (
  last_over_time(
    (
      label_replace(
        kube_pod_container_info{cluster="$cluster", namespace="$namespace", pod="$pod", container=~"$container", container_id=~"containerd://($id)"},
        "name", "$1", "container_id", "containerd://(.*)"
      )
      * on(pod, container, uid)
      kube_pod_container_resource_limits{cluster="$cluster", namespace="$namespace", pod="$pod", container=~"$container", resource="memory"}
    )
  [$__range:])
)

max by (container, name) (
  last_over_time(
    (
      container_memory_working_set_bytes{cluster="$cluster", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}
      / on(container) group_left(uid)
      kube_pod_container_resource_limits{cluster="$cluster", namespace="$namespace", pod="$pod", container=~"$container", resource="memory"}
    )
  [$__range:])
)

max by (container, name) (
  last_over_time(container_last_seen_time{cluster="$cluster", namespace="$namespace", pod=~"$pod", uid!=""}[$__range])
) * 1000
