targets.expr:
sum by (workload_type, workload, container) (
    min by (cluster, namespace, pod, container) (min_over_time(kube_pod_container_status_ready{job="kube-state-metrics", cluster="$cluster", namespace="$namespace"}[$__rate_interval]))
    * on(cluster, namespace, pod) group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{cluster="$cluster", namespace="$namespace", workload="$workload", workload_type="deployment"}
) / scalar (kube_deployment_spec_replicas{cluster="$cluster", namespace="$namespace", deployment="$workload"})

sum by (workload_type, workload, container) (
    min by (cluster, namespace, pod, container) (min_over_time(kube_pod_container_status_ready{job="kube-state-metrics", cluster="$cluster", namespace="$namespace"}[$__rate_interval]))
    * on(cluster, namespace, pod) group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{cluster="$cluster", namespace="$namespace", workload="$workload", workload_type="daemonset"}
) / scalar (kube_daemonset_status_desired_number_scheduled{cluster="$cluster", namespace="$namespace", daemonset="$workload"})
