targets.expr:
max by(instance, zpool, vdev, path, type) (
  label_replace(
    rate(zfs_vdev_errors{instance=~"$instance", zpool=~"$pool", path!="", vdev=~"$vdev", path=~"$disk", type="read"}[$__rate_interval])
    , "path", "$1", "path", ".*/([^/]*)"
  )
) * $__interval_ms / 1000

max by(instance, zpool, vdev, path, type) (
  label_replace(
    rate(zfs_vdev_errors{instance=~"$instance", zpool=~"$pool", path!="", vdev=~"$vdev", path=~"$disk", type="write"}[$__rate_interval])
    , "path", "$1", "path", ".*/([^/]*)"
  )
) * $__interval_ms / 1000

max by(instance, zpool, vdev, path, type) (
  label_replace(
    rate(zfs_vdev_errors{instance=~"$instance", zpool=~"$pool", path!="", vdev=~"$vdev", path=~"$disk", type="checksum"}[$__rate_interval])
    , "path", "$1", "path", ".*/([^/]*)"
  )
) * $__interval_ms / 1000

max by(instance, zpool, vdev, path, type) (
  label_replace(
    rate(zfs_vdev_errors{instance=~"$instance", zpool=~"$pool", path!="", vdev=~"$vdev", path=~"$disk", type="initialize"}[$__rate_interval])
    , "path", "$1", "path", ".*/([^/]*)"
  )
) * $__interval_ms / 1000

max by(instance, zpool, vdev, path, type) (
  label_replace(
    rate(zfs_vdev_trim_errors{instance=~"$instance", zpool=~"$pool", path!="", vdev=~"$vdev", path=~"$disk"}[$__rate_interval])
    , "path", "$1", "path", ".*/([^/]*)"
  )
) * $__interval_ms / 1000

$__to / 1000 - max by(zpool, vdev, path) (
  label_replace(
    max_over_time(zfs_vdev_trim_action_time{instance=~"$instance", zpool=~"$pool", vdev=~"$vdev", path=~"$disk"}[$__interval]) != 0
    , "path", "$1", "path", ".*/([^/]*)"
  )
)

max by(zpool, vdev, path) (
  label_replace(
    max_over_time(zfs_vdev_trim_estimated_bytes{instance=~"$instance", zpool=~"$pool", path=~"$disk"}[$__interval]) != 0
    , "path", "$1", "path", ".*/([^/]*)"
  )
)
